INSERT INTO [dbo].[Movies] ([ID], [Name], [Image], [Highlighted], [Description], [Trailer], [LongDescription], [ImageRepertoar]) VALUES (1, N'Captain Marvel', N'/images/capt2.jpg', 1, N'Kapetan Marvel, najnoviji film iz Marvel univerzuma smesten u 90-im godinama.', N'<iframe width="560" height="315" src="https://www.youtube.com/embed/Z1BCujX3pw8" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>', N'Smešten u devedesete godine dvadesetog veka, Kapetan Marvel iz Marvelovog studija je potpuno nova avantura iz do sada neviđenog perioda u istoriji Marvelovog stripskog univerzuma, koja prati Kerol Denvers kako postaje jedna od najznačajnijih junakinja u univerzumu. Dok galaktički rat koji vode dve vanzemaljske rase utiče i na Zemlju, Denvers će se naći, zajedno sa malom grupom saveznika u centru vrtloga.', NULL)
INSERT INTO [dbo].[Movies] ([ID], [Name], [Image], [Highlighted], [Description], [Trailer], [LongDescription], [ImageRepertoar]) VALUES (3, N'Blackkkansman', N'/images/black.jpg', 1, N'Blackkklansman, novi film Spajk Lija, baziran na istinitim dogadjajima.', N'<iframe width="560" height="315" src="https://www.youtube.com/embed/pFc6I0rgmgY" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>', N'Od vizionarnog reditelja Spajk Lija dolazi neverovatna istinita priča o američkom heroju. Radnja se dešava u ranim 70-im, Ron Stalvort (Džon Dejvid Vošington) je prvi afro-američki detektiv koji služi u Kolorado Springs policiji. Odlučan u nameri da se istakne i napravi napredak u karijeri, Stalvort hrabro stupa u opasnu misiju koja uključuje infiltraciju i razotkrivanje Kju Kluks Klana. Mladi detektiv ubrzo regrutuje iskusnijeg kolegu, Flipa Zimermana (Adam Drajver), u tajnu policijsku istragu njihovih života. Zajedno prave tim da sruše ekstremističku zločinačku grupu koja kao organizacija ima za cilj da svoju nasilnu retoriku pretvori u mejnstrim.', NULL)
INSERT INTO [dbo].[Movies] ([ID], [Name], [Image], [Highlighted], [Description], [Trailer], [LongDescription], [ImageRepertoar]) VALUES (5, N'Zelena knjiga', N'/images/green-book.jpg', 1, N'Zelena knjiga, film koji je osvojio Oskara za najbolji film 2019-e.', N'<iframe width="560" height="315" src="https://www.youtube.com/embed/QkZxoko_HC0" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>', N'Toni Lip, opaki izbacivač iz noćnog kluba u potrazi je za novim poslom. Ponuda koja najviše obećava je mesto vozača afro-američkog klasičnog pijaniste Dona Širlija na koncertnoj turneji kroz krajnji jug zemlje. Nimalo oduševljen što će raditi za crnca, Toni ipak prihvata posao i počinje put naoružan Zelenom knjigom, vodičem za sigurno putovanje kroz američku rasnu segregaciju. Snobovski učen pijanista i grubi izbacivač jedva da pronalaze zajednički jezik zbog suprotstavljenih stavova prema životu i idealima. Međutim, dok svedoče i trpe strašne nepravde na putu, njih dvojica pronalaze novo poštovanje prema talentima i srcu onog drugog. Među njima se razvijaju prijateljstvo i razumevanje koji će im promeniti živote. Glavne uloge tumače Vigo Mortensen i Maheršala Ali. Režiju potpisuje Piter Fareli inspirisan pričom o istinskom prijateljstvu koje je nadmašilo rasne, klasne i društvene prepreke ranih 60-tih godina u Americi. ', NULL)
INSERT INTO [dbo].[Movies] ([ID], [Name], [Image], [Highlighted], [Description], [Trailer], [LongDescription], [ImageRepertoar]) VALUES (8, N'Kako da dresirate svog zmaja 3D', N'/images/zmaj.jpg', 1, N'Najnoviji nastavak animiranog filma Kako da dresirate svog zmaja 3, Skriveni svet u 3D-u.', N'<iframe width="560" height="315" src="https://www.youtube.com/embed/SkcucKDrbOI" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>', N'Iz studija DreamWorks Animation stiže nam epska animirana avantura o odrastanju i pronalaženju hrabrosti da se suočimo sa nepoznatim i o tome kako ne treba odustati. Ono što će početi kao neočekivano prijateljstvo između mladog Vikinga i neustrašivog zmaja pretvoriće se u avanturu koja prevazilazi i sam život. Bioskopska publika imaće priliku da uživa u novom filmu jedne od najomiljenijih animiranih saga svih vremena!
INSERT INTO [dbo].[Movies] ([ID], [Name], [Image], [Highlighted], [Description], [Trailer], [LongDescription], [ImageRepertoar]) VALUES (9, N'Crni Andjeo', N'/images/angel.jpg', 1, N'Crni andjeo je argentiska krimi drama inspirisan istinitom pričom o poznatom serijskom ubici Karlos Rodregu Puču.', N'<iframe width="560" height="315" src="https://www.youtube.com/embed/0ypjYdv8L2M" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>', N'Ovo je argentinski kandidat za Oskara i govori o serijskom ubici koji je trenutno poznat kao najduže zatočeni zatvorenik u argentinskom zatvoru. Debitant Lorenco Fero našao se u glavnoj ulozi okrutnog tinejdžera Karlosa, koji kreće na krvavu avanturu sa svojim najboljim prijateljem. Za razliku od većine filmova o serijskim ubicama, Ortega se ne udubljuje previše u psihologiju i ne traži opravdanje u mračnoj prošlosti - Karlos je obožavan od malih nogu, odrastao je sasvim srećan, i izgleda kao pravi anđeo. Kako neko toliko lep može da bude toliko ružan iznutra? Ovaj film pruža fascinantan uvid u svet jednog od najozloglašenijih argentinskih zločinaca. Ortega na interesantan način tretira ovu mračnu figuru i stvara film koji je visoko stilizovan, okupan suncem i svetlim bojama, i oplemenjen momentima humora. Ovo je subverzivan krimi triler koji na inovativan način ruši konvencije žanra. ', NULL)
INSERT INTO [dbo].[Movies] ([ID], [Name], [Image], [Highlighted], [Description], [Trailer], [LongDescription], [ImageRepertoar]) VALUES (10, N'Boemska rapsodija', N'/images/bohem1.jpg', 1, N'Boemska rapsodija je biografski film koji slavi grupu Queen, njihovu muziku i izvanrednog frontmena Fredija Merkjurija, koji je prkošenjem i razbijanjem stereotipa postao jedan od najobožavanijih zabavljača na planeti.', N'<iframe width="560" height="315" src="https://www.youtube.com/embed/mP0VHJYFOAU" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>', N'„Boemska rapsodija“ je biografski film koji slavi grupu Queen, njihovu muziku i izvanrednog frontmena Fredija Merkjurija, koji je prkošenjem i razbijanjem stereotipa postao jedan od najobožavanijih zabavljača na planeti. Kroz presek kultnih pesama i revolucionarnog zvuka, film takođe prati i meteorski brz razvoj i uspon benda, uključujući i eksploziju tog istog meteora u vreme kada se Fredijev životni stil oteo kontroli, ali i trijumfalni povratak koncertom na Live Aid-u, kada je Merkjuri, gledajući u oči svoju smrtnu bolest, poveo bend u jedan od najznačajnijih i najfascinantnijih nastupa u istoriji rok muzike. Uz stalno jačanje nasleđa benda, koji je uvek bio više kao porodica, grupa Queen u svakom trenutku, pa i danas, neiscrpno nadahnjuje brojne sanjare, autsajdere i ljubitelje muzike.', NULL)
